import { Component, OnInit, ViewChild, ElementRef } from '@angular/core';

@Component({
  selector: 'app-root',
  standalone: true,
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  @ViewChild('canvas', { static: true }) canvas!: ElementRef<HTMLCanvasElement>;
  private ctx!: CanvasRenderingContext2D;
  private grid: number[][] = [];
  private rows = 8;
  private cols = 8;
  private start = { x: 0, y: 0 };
  private goal = { x: 7, y: 7 };
  private visited: boolean[][] = [];
  private path: { x: number, y: number }[] = [];
  private visitedOrder: { x: number, y: number }[] = [];
  private isAnimating = false;

  ngOnInit() {
    this.ctx = this.canvas.nativeElement.getContext('2d')!;
    this.initGrid();
    this.drawGrid();
  }

  initGrid() {
    for (let i = 0; i < this.rows; i++) {
      this.grid[i] = [];
      this.visited[i] = [];
      for (let j = 0; j < this.cols; j++) {
        this.grid[i][j] = 0; // 0 open, 1 wall
        this.visited[i][j] = false;
      }
    }
    // add some walls
    this.grid[1][1] = 1;
    this.grid[2][2] = 1;
    this.grid[3][1] = 1;
  }

  drawGrid() {
    const cellSize = 100;
    this.ctx.clearRect(0, 0, 500, 500);
    for (let i = 0; i < this.rows; i++) {
      for (let j = 0; j < this.cols; j++) {
        this.ctx.strokeRect(j * cellSize, i * cellSize, cellSize, cellSize);
        if (this.grid[i][j] === 1) {
          this.ctx.fillStyle = 'black';
          this.ctx.fillRect(j * cellSize, i * cellSize, cellSize, cellSize);
        }
        if (i === this.start.y && j === this.start.x) {
          this.ctx.fillStyle = 'green';
          this.ctx.fillRect(j * cellSize + 10, i * cellSize + 10, cellSize - 20, cellSize - 20);
        }
        if (i === this.goal.y && j === this.goal.x) {
          this.ctx.fillStyle = 'red';
          this.ctx.fillRect(j * cellSize + 10, i * cellSize + 10, cellSize - 20, cellSize - 20);
        }
      }
    }
  }

  runDFS() {
    this.resetVisited();
    this.path = [];
    this.dfs(this.start.x, this.start.y);
    this.animatePath();
  }

  runBFS() {
    this.resetVisited();
    this.path = [];
    this.bfs();
    this.animatePath();
  }

  resetVisited() {
    for (let i = 0; i < this.rows; i++) {
      for (let j = 0; j < this.cols; j++) {
        this.visited[i][j] = false;
      }
    }
  }

  dfs(x: number, y: number): boolean {
    if (x < 0 || x >= this.cols || y < 0 || y >= this.rows || this.grid[y][x] === 1 || this.visited[y][x]) {
      return false;
    }
    this.visited[y][x] = true;
    this.path.push({ x, y });
    if (x === this.goal.x && y === this.goal.y) {
      return true;
    }
    // up, down, left, right
    if (this.dfs(x, y - 1)) return true;
    if (this.dfs(x + 1, y)) return true;
    if (this.dfs(x, y + 1)) return true;
    if (this.dfs(x - 1, y)) return true;
    this.path.pop();
    return false;
  }

  bfs() {
    const queue: { x: number, y: number, path: { x: number, y: number }[] }[] = [];
    queue.push({ x: this.start.x, y: this.start.y, path: [{ x: this.start.x, y: this.start.y }] });
    this.visited[this.start.y][this.start.x] = true;
    while (queue.length > 0) {
      const current = queue.shift()!;
      if (current.x === this.goal.x && current.y === this.goal.y) {
        this.path = current.path;
        return;
      }
      // up, down, left, right
      const directions = [
        { dx: 0, dy: -1 },
        { dx: 1, dy: 0 },
        { dx: 0, dy: 1 },
        { dx: -1, dy: 0 }
      ];
      for (const dir of directions) {
        const nx = current.x + dir.dx;
        const ny = current.y + dir.dy;
        if (nx >= 0 && nx < this.cols && ny >= 0 && ny < this.rows && this.grid[ny][nx] === 0 && !this.visited[ny][nx]) {
          this.visited[ny][nx] = true;
          queue.push({ x: nx, y: ny, path: [...current.path, { x: nx, y: ny }] });
        }
      }
    }
  }

  animatePath() {
    let index = 0;
    const interval = setInterval(() => {
      if (index < this.path.length) {
        const cell = this.path[index];
        this.ctx.fillStyle = 'blue';
        this.ctx.fillRect(cell.x * 100 + 20, cell.y * 100 + 20, 60, 60);
        index++;
      } else {
        clearInterval(interval);
      }
    }, 500);
  }
}
